import { Fragment, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext"

export default function Login() {
    const [userName, setUserName] = useState('')
    const [password, setPassword] = useState('')
    const { doLogin } = useContext(AuthContext)
    const navigate = useNavigate()

    function validateUser() {
        if (userName === "Admin" && password === "admin") {
            doLogin()
        } else{
            window.alert("Usu치rio e/ou senha inv치lidos")
        }
        navigate('/dragons')
    }

  return (
  <Fragment>
    <div className="login">
      <div className="login-container">
        <div className="login-info">
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Blanditiis, esse? Eligendi reprehenderit magni cupiditate sed quasi qui aut sit, nam doloribus omnis veniam amet accusamus sequi excepturi repudiandae iusto quidem?</p>
        </div>
        <div className="login-form">
          <h1>Login</h1>
          <p>Nome:</p>
          <input type="text"
              onChange={(e) => {
                  setUserName(e.target.value)
              }}
          />
          <p>Senha:</p>
          <input type="password"
              onChange={(e) => {
                  setPassword(e.target.value)
              }}
          />
          <button
              onClick={() => {
                  validateUser();
              }}
          >Entrar</button>
        </div>
      </div>
    </div>
  </Fragment>
  )
}
